import{_ as a}from"./chunks/theme.CoJNsPLp.js";import{_ as i,c as l,o,aj as t}from"./chunks/framework.BBkT2Rd3.js";const f=JSON.parse('{"title":"Changelog","description":"","frontmatter":{},"headers":[],"relativePath":"Main/Changelogs/Updates/v1.0.3/v1.0.3.8.md","filePath":"Main/Changelogs/Updates/v1.0.3/v1.0.3.8.md"}'),s={name:"Main/Changelogs/Updates/v1.0.3/v1.0.3.8.md"};function r(n,e,d,h,u,c){return o(),l("div",null,e[0]||(e[0]=[t('<p><img src="'+a+'" alt="image"></p><h1 id="changelog" tabindex="-1">Changelog <a class="header-anchor" href="#changelog" aria-label="Permalink to &quot;Changelog&quot;">​</a></h1><p>Better on bedrock Changelog v1.0.3.8</p><h2 id="player-reported-bugs-that-you-ll-see-below-in-fixes" tabindex="-1">Player reported bugs that you&#39;ll see below in Fixes <a class="header-anchor" href="#player-reported-bugs-that-you-ll-see-below-in-fixes" aria-label="Permalink to &quot;Player reported bugs that you&#39;ll see below in Fixes&quot;">​</a></h2><h3 id="fixes" tabindex="-1">Fixes <a class="header-anchor" href="#fixes" aria-label="Permalink to &quot;Fixes&quot;">​</a></h3><ul><li>Fixed an issue where backpacks would not open when the player used a waystone</li><li>Fixed an issue where Forest leaves would decay</li><li>Fixed an issue that caused the Resistance potion to not give effects</li><li>Fixed an issue where tool did not take durability when breaking blocks</li><li>Fixed the issues where Minecraft would crash</li><li>Fixed an issue where some Armor did not have protection</li><li>Fixed an issue where Willager Clones did not despawn</li><li>Fixed an issue where the Enchantaegis did not do a knockback attack</li><li>Fixed an issue where the Lilac Flower had a white box around it</li><li>Fixed an issue where cows were invisible</li><li>Fixed an issue where the Combined Elements item was not craftable</li><li>Fixed an issue where players could not craft Fungus Stew</li><li>Fixed an issue where players could not craft Lava Salad</li><li>Fixed an issue where Forest Trees were placed on a fixed grid</li><li>Fixed an issue where the Nether Amulet had no model</li><li>Fixed the issue where Stardust ore did not drop any items</li><li>Fixed the text of the Tall Lavender</li></ul><h3 id="tweaks" tabindex="-1">Tweaks <a class="header-anchor" href="#tweaks" aria-label="Permalink to &quot;Tweaks&quot;">​</a></h3><ul><li>Tools work properly with efficiency again</li><li>Tools properly loose durability when attacking mobs.</li><li>Flender can no longer be stuck in a boat.</li><li>Pigeons now fly correctly</li><li>Pigeons now drop the correct loot</li></ul><h3 id="new-features" tabindex="-1">New Features <a class="header-anchor" href="#new-features" aria-label="Permalink to &quot;New Features&quot;">​</a></h3><ul><li>Added 2 Traail Ruins <ul><li>Found in birch forests</li></ul></li><li>Use shears to remove flowers</li></ul><h3 id="changes" tabindex="-1">Changes <a class="header-anchor" href="#changes" aria-label="Permalink to &quot;Changes&quot;">​</a></h3><ul><li>Removed a lot of unused files</li><li>Removed Polished Tuff Recipes</li><li>Reduced the rarity of the Trader Outpost</li><li>Reduced the amount at which the new music plays</li><li>Reduces the breaking speed of Waystones</li><li>Reworked hanging pots <ul><li>you can now use any flower ( not custom ) on the blocks to apply it.</li></ul></li><li>Re-added Tall Lavender, Blugrod and Lush Grass to generate in birch and forest biomes</li><li>Re-Added some vanilla biomes trees</li><li>Vanilla Flower pots will not work.</li><li>Updated outdated Bee code to allow them to pollinate again</li><li>Updated birch and forest biome to have more foliage.</li></ul><h3 id="changes-made-via-survey-suggestions" tabindex="-1">Changes Made Via Survey Suggestions <a class="header-anchor" href="#changes-made-via-survey-suggestions" aria-label="Permalink to &quot;Changes Made Via Survey Suggestions&quot;">​</a></h3><ul><li>Reduced the rate at which corstinite generates</li><li>Increased the Power of some magic staffs</li><li>Reduced the cooldown of magic staffs</li><li>The &quot;Blade Of The Nether&quot; has a 70% of dropping from a Pyroclast</li></ul>',14)]))}const p=i(s,[["render",r]]);export{f as __pageData,p as default};
