import{p as i}from"./chunks/theme.5raFyHhN.js";import{_ as a,c as l,o as t,aj as s}from"./chunks/framework.BBkT2Rd3.js";const p=JSON.parse('{"title":"Changelog","description":"","frontmatter":{},"headers":[],"relativePath":"Main/Changelogs/Updates/v1.0.4/v1.0.4.md","filePath":"Main/Changelogs/Updates/v1.0.4/v1.0.4.md"}'),o={name:"Main/Changelogs/Updates/v1.0.4/v1.0.4.md"};function r(n,e,d,h,u,c){return t(),l("div",null,e[0]||(e[0]=[s('<p><img src="'+i+'" alt="image"></p><h1 id="changelog" tabindex="-1">Changelog <a class="header-anchor" href="#changelog" aria-label="Permalink to &quot;Changelog&quot;">​</a></h1><p>Better on Bedrock Reworked Changelog</p><h2 id="player-reported-bugs-that-you-ll-see-below-in-fixes" tabindex="-1">Player reported bugs that you&#39;ll see below in Fixes <a class="header-anchor" href="#player-reported-bugs-that-you-ll-see-below-in-fixes" aria-label="Permalink to &quot;Player reported bugs that you&#39;ll see below in Fixes&quot;">​</a></h2><h3 id="fixes" tabindex="-1">Fixes <a class="header-anchor" href="#fixes" aria-label="Permalink to &quot;Fixes&quot;">​</a></h3><ul><li>Fixed some slang and typos in some names of items</li><li>Fixed the missing texture of the Hanging Tulip flower Pot</li><li>Fixed an issue where tools would not break once they have no durability left</li><li>Fixed an issue where the Flender would not attack the player and stay high in the air</li><li>Fixed an issue where the Enchantaegis could be hit whilst casting a spell</li><li>Fixed an issue where the Dotted Sheep could be dyed and not grow their wool back</li><li>Fixed an issue where Armor Trims could not be made</li><li>Fixed issues where Structures would collide with other structures</li><li>Fixed an issue where the Waystone Tower could be below surface level</li><li>Fixed mob spawning</li><li>Fixed the issue where bosses would attack when you pass by them at a far distance</li><li>Fixed issues where some bosses would attack the player in creative mode</li><li>Fixed an issue where the Flender did not rotate when they swoop the player</li><li>Fixed an issue where Leafy Libirator did not drop the intended amound of leaves</li><li>Fixed an issue where most crops could not be bonemealed</li><li>Fixed the heart icon</li><li>Fixed an issue where players were unable to craft dye from flowers</li><li>Fixed an issue where the Quetzacaw Egg could not be found</li><li>Fixed odd visuals on Peach and Orange leaves</li><li>Fixed Swamp Trees having their leaves decay, causing FPS issues</li><li>Fixed moss generating above water at Birch Forests</li><li>Fixed issues where new Trail Ruins did not generate in Savanna biomes</li><li>Fixed an issue where Meadow Trees failed to place</li><li>Fixed an issue where players could run normal speed while eating foods</li><li>Fixed an issue where the waystone did not promt an error message when the player had no levels</li><li>Fixed a lot of cases where quests showed incorrect rewards</li><li>Fixed issues where Mobs in the Nether would spawn everywhere</li></ul><h3 id="tweaks" tabindex="-1">Tweaks <a class="header-anchor" href="#tweaks" aria-label="Permalink to &quot;Tweaks&quot;">​</a></h3><ul><li>Allowed Boss Structures to be found in more biomes</li><li>Crafting a Stardsut Ingot gives 4 ingots instead of 1</li><li>Players are able to buy and find new foods in many places</li><li>The waystone is now placed with an item</li><li>Made some tweaks to mob spawn rates (custom mobs)</li><li>Bosses have been tweaked in their Attacks, Attack Speed and Health</li></ul><h3 id="new-features" tabindex="-1">New Features <a class="header-anchor" href="#new-features" aria-label="Permalink to &quot;New Features&quot;">​</a></h3><ul><li>Added 2 new Attacks to the Enchantaegis</li><li>Players are able to buy quests via the Librarian Villager <ul><li>There are a total of 16 quests you can buy</li><li>Players get a random quest. Already unlocked quests will grant xp!</li><li>Quests Rewards are given on quest complete!</li></ul></li><li>Added new rewards to quests <ul><li>These rewards consist of a loot bag that can drop random loot</li></ul></li><li>Added a Random Lootbag that is provided by completing certain quests</li><li>Added Pedestals that can be used to display items</li><li>Added the &quot;Rare Quest&quot; sound to all bosses</li></ul><h3 id="changes" tabindex="-1">Changes <a class="header-anchor" href="#changes" aria-label="Permalink to &quot;Changes&quot;">​</a></h3><ul><li>Updated quest description to be more clear</li><li>Updated the Waystone to consist of 2 blocks when placed</li><li>Updated the Waystone to be more basic</li><li>Updated the way players use the Waystone</li><li>Updated the Miner Bench to have a more clear description</li><li>Updated some quest descriptions and made requirements more clear</li><li>Reworked and re-added vitality stone <ul><li>The stone now grants regenerations with a cooldwon</li></ul></li><li>Reworked the Nether Amulet <ul><li>You now have to sneak to choose the type of stone to use</li><li>Amulets that has one or two stones won&#39;t work</li></ul></li><li>Reworked the Ghost Necklace to drop in fragments from the Flender</li><li>Reworked Trophies to now hold the head of the boss it was dropped from</li><li>Reworked the way player buy/activate runes. <ul><li>Runes have been remove and have been replaced by Magic Scroll.</li><li>These scrolls can be found in loot and buy trading with the wizard</li><li>The scrolls will tell you how to use them</li></ul></li><li>Reworked all Loot Tables for structure and mobs</li><li>Changed the way Magic Wands behave</li><li>Racoons no longer dig for loot as often</li><li>Stardust is much easier to obtain</li><li>Overhauled Magic wands to be more useful during combat</li><li>Overhauled the Taiga and Forest trees</li><li>Reduced the screen shake when fighting the Shielded Pryoclast</li><li>Made some adjustments to some textures</li><li>Removed the Vitality Staff</li><li>Removed the special brush crafting recipe</li><li>Removed &quot;unactived&quot; runes</li><li>Removed some items that were supposed to be hidden = Removed Corrupted Villager</li></ul>',12)]))}const g=a(o,[["render",r]]);export{p as __pageData,g as default};
