import{_ as a}from"./chunks/bob-rebrand.8TJOUA40.js";import{_ as l,c as t,o,aj as i}from"./chunks/framework.BBkT2Rd3.js";const b=JSON.parse('{"title":"Changelog","description":"","frontmatter":{},"headers":[],"relativePath":"Main/Changelogs/Updates/v1.0.5/v1.0.5.5.md","filePath":"Main/Changelogs/Updates/v1.0.5/v1.0.5.5.md"}'),r={name:"Main/Changelogs/Updates/v1.0.5/v1.0.5.5.md"};function s(n,e,d,h,u,c){return o(),t("div",null,e[0]||(e[0]=[i('<p><img src="'+a+'" alt="image"></p><h1 id="changelog" tabindex="-1">Changelog <a class="header-anchor" href="#changelog" aria-label="Permalink to &quot;Changelog&quot;">​</a></h1><p>Better on bedrock Changelog v1.0.5.5</p><h2 id="player-reported-bugs-that-you-ll-see-below-in-fixes" tabindex="-1">Player reported bugs that you&#39;ll see below in Fixes <a class="header-anchor" href="#player-reported-bugs-that-you-ll-see-below-in-fixes" aria-label="Permalink to &quot;Player reported bugs that you&#39;ll see below in Fixes&quot;">​</a></h2><h3 id="fixes" tabindex="-1">Fixes <a class="header-anchor" href="#fixes" aria-label="Permalink to &quot;Fixes&quot;">​</a></h3><ul><li>Fixed a bug where the miner bench failed to work correctly</li><li>Fixed an issue where any item with durability would take damage when player attacked mobs or broke blocks while holding these items</li><li>Fixed a bug that caused unbreaking to not work on tools</li><li>Fixed a bug that caused the coconut to randomly flood player inventories</li><li>Full amethyst armor now grants its bonus effect [Bug]</li><li>Fixed a bug that prevented Player Corpse to work properly</li><li>Fixed up librarian villager trades <ul><li>The chance to get a bought quest or any of the new enchantent is much lower</li></ul></li><li>Fixed an issue that caused the pedestal to randomly loose items</li><li>Fixed a bug that caused the Willager chest to not summon the Willager</li><li>Fixed a bug that caused Amethys Armor to play sounds to all players</li><li>Fixed a bug that allowed players to obtain loads of wool from the Dotted Sheep</li><li>Fixed sounds for all end wood types</li><li>Fixed the &quot;?&quot; block that appeared wihtin a structure</li><li>Fixed a bug that allowed players to obtain a waystone block when using Silk Touch <ul><li>This also fixed a duplication bug</li></ul></li><li>Voided boots now have their correct behavior thanks to a bug fixed from Mojang</li><li>Resolved some issues with the Waystone Key</li></ul><h3 id="tweaks" tabindex="-1">Tweaks <a class="header-anchor" href="#tweaks" aria-label="Permalink to &quot;Tweaks&quot;">​</a></h3><ul><li>Enchanted books added by the Add-On can no longer be stacked</li></ul><h3 id="new-features" tabindex="-1">New Features <a class="header-anchor" href="#new-features" aria-label="Permalink to &quot;New Features&quot;">​</a></h3><h3 id="changes" tabindex="-1">Changes <a class="header-anchor" href="#changes" aria-label="Permalink to &quot;Changes&quot;">​</a></h3><ul><li>Updated tools added by the Add-On to use custom components</li><li>Updated Grape and Berry bushes to match vanilla generation</li><li>Updated Peach and Orange leaves to follow vanilla behavior</li><li>Updated Small Logs to be placed sideways</li><li>Updated end logs to behave like vanilla logs</li><li>Updated some food/seed items to work with composters. More will be added in the future! <ul><li>Anenome Flower</li><li>Baguette</li><li>Baked Eggplant</li><li>Barley Seeds</li><li>Barley Straw</li><li>Bluegrod Flower</li><li>Cabbage Seeds</li><li>Grape Pie</li><li>Healthy Carrot</li></ul></li><li>Removed all instance of console.warn from scripts. This means for players who run a server or play with content errors enabled, won&#39;t have their console spammed with debug or test logs</li></ul><h3 id="misc" tabindex="-1">Misc <a class="header-anchor" href="#misc" aria-label="Permalink to &quot;Misc&quot;">​</a></h3><ul><li>Mostly cleaned up the error console when loading a world</li><li>Updated the Add-On to work properly in 1.21.0</li></ul>',13)]))}const m=l(r,[["render",s]]);export{b as __pageData,m as default};
