import{p as l}from"./chunks/theme.cDnWdXLL.js";import{_ as i,c as a,o,aj as t}from"./chunks/framework.BBkT2Rd3.js";const f=JSON.parse('{"title":"Changelog","description":"","frontmatter":{},"headers":[],"relativePath":"Main/Changelogs/Updates/v1.0.5/v1.0.5.md","filePath":"Main/Changelogs/Updates/v1.0.5/v1.0.5.md"}'),s={name:"Main/Changelogs/Updates/v1.0.5/v1.0.5.md"};function r(n,e,d,u,h,c){return o(),a("div",null,e[0]||(e[0]=[t('<p><img src="'+l+'" alt="image"></p><h1 id="changelog" tabindex="-1">Changelog <a class="header-anchor" href="#changelog" aria-label="Permalink to &quot;Changelog&quot;">​</a></h1><p>Better on Bedrock Ender Depths Changelog</p><h2 id="player-reported-bugs-that-you-ll-see-below-in-fixes" tabindex="-1">Player reported bugs that you&#39;ll see below in Fixes <a class="header-anchor" href="#player-reported-bugs-that-you-ll-see-below-in-fixes" aria-label="Permalink to &quot;Player reported bugs that you&#39;ll see below in Fixes&quot;">​</a></h2><h3 id="fixes" tabindex="-1">Fixes <a class="header-anchor" href="#fixes" aria-label="Permalink to &quot;Fixes&quot;">​</a></h3><ul><li>Fixed the Forger</li><li>Fixed an issue where Deer Meat had the wrong texture</li><li>Fixed a case where Blazes would always drop souls</li><li>Fixed the issue where the Flender Staff recipe required souls with NBT data of 1</li><li>Fixed an issue where players would only get a regular Iron Pickaxe</li><li>Fixed issues where Fortune and Silk Touch refused to work with Vein Miner</li><li>Fixed incorrect textures on some items</li><li>Fixed an issue where the day counter would continue counting once daylight cylce is false and set to 0</li><li>Fixed basic staffs being able to stack</li><li>Fixed an issue where sus-dirt would spam the chat with messages</li></ul><h3 id="new-features" tabindex="-1">New Features <a class="header-anchor" href="#new-features" aria-label="Permalink to &quot;New Features&quot;">​</a></h3><ul><li>Added 5 End Biomes <ul><li>Vacant Dusk</li><li>Voiding Plains</li><li>Voided Abyss</li><li>Fungal Grove</li><li>Chorus Forest</li><li>End Biomes will not appear at the boss arena</li></ul></li><li>Added 19 new Mobs to the end <ul><li>Chorus Forest: <ul><li>Chorus Behemith</li><li>Chorus Wanderer</li><li>Chorus Golem</li></ul></li><li>Voiding Plains: <ul><li>Fireorb</li><li>Ender Merchant</li><li>Groohog</li></ul></li><li>Fungal Grove: <ul><li>Bacterio</li><li>Infected</li><li>Shroomy</li></ul></li><li>Vacant Dusk: <ul><li>Jellyfly</li><li>Longleg</li><li>Mooth</li><li>Squidy</li></ul></li><li>End Wastes: <ul><li>Hover</li></ul></li><li>Boss: <ul><li>Soot Collector</li><li>Seeker</li><li>Poggy</li></ul></li></ul></li><li>Added Enderite Ingot and Tools <ul><li>Enderite tools are better than Stardust tools</li><li>Players are required to find End Scrolls that are found within stucture chest and pots</li></ul></li><li>Added Shulker Shell Armor <ul><li>Shulker Shell Armor passifies Endermen and Shulkers</li><li>Requires a full set to take effect</li></ul></li><li>Added 7 new structures to the end</li><li>Added 6 new foods: <ul><li>Chorus Flitter</li><li>Chorus Pie</li><li>End Fruit</li><li>End Cookie</li><li>Ender Stew</li><li>Void Berrt Pie</li></ul></li><li>Added 9 Useful Items: <ul><li>Ring of Hope</li><li>Ring of Hate</li><li>Ring of Care</li><li>Multi Ring (combined with 3 rings) <ul><li>These rings grant loot when used on Basic Podiums</li></ul></li><li>Void Totem <ul><li>Deals 17 Damage when used</li><li>Gives Nausea &amp; Blindness for 10 seconds</li></ul></li><li>Void Boots <ul><li>Takes damage while you are above the void</li><li>Can&#39;t be crafted, repaired or enchanted</li></ul></li><li>End Scroll</li><li>Enderite Ingot</li><li>Enderium</li><li>Bane of Spike <ul><li>Grants poison to any hit target</li></ul></li><li>Combustible Scrab -Fragments Of Soul</li><li>Legrang</li><li>Persisting Embodiment</li><li>Schroom Sample</li><li>Potion of Rage</li></ul></li><li>Added over 100 new blocks: <ul><li>Malbite + 5 other variants <ul><li>Included slabs</li></ul></li><li>New Wood sets based on the biomes <ul><li>Does include doors</li><li>Includes slabs</li></ul></li><li>New Flowers for decoration</li></ul></li><li>Added new Boss Loot <ul><li>The Bosses of The End will drop shards. These shards are used to create a type of item that Players can use to Summon Poggy, the Final Boss of Better on Bedrock</li></ul></li></ul><h3 id="changes" tabindex="-1">Changes <a class="header-anchor" href="#changes" aria-label="Permalink to &quot;Changes&quot;">​</a></h3><ul><li>Updated some item textures</li><li>Changed the way Players obtain Stardust Armor <ul><li>Players are now required to use Persisting Embodiment to craft Stardust Armor.</li><li>This makes it so Players can&#39;t get Stardust in only the Overworld, making their playthrough easier</li></ul></li><li>Added more variants of crafting tables</li><li>Removed the Custom Jukebox</li><li>Converted the Custom Discs to be actual record discs</li><li>Added a new slider to the Add-On config menu</li><li>Update the day counter text to be colorful</li></ul><h3 id="misc" tabindex="-1">Misc <a class="header-anchor" href="#misc" aria-label="Permalink to &quot;Misc&quot;">​</a></h3>',11)]))}const b=i(s,[["render",r]]);export{f as __pageData,b as default};
